import { useState } from 'react';
import FormHeader from "./FormHeader";
import FormElement from "./FormElement";
function CreateEvent(){
    const [inputs, setInputs]=useState({})
    function handleChange(e){
        const name=e.target.name;
        const value=e.target.value;
        setInputs({...inputs, [name]:value});
    }
    function handleEnter(e){
        if(e.target.name==="Event Title"){
            document.getElementsByName('Date')[0].focus();
        }
        if(e.target.name==="Email"){
            document.getElementsByName('Password')[0].focus();
        }
        if(e.target.name==="Password"){
            // To je isto kao i pritisak na Sign in
        }

    }
    return(
        <div style={{
            boxSizing:"border-box",
            marginLeft: "auto",
            marginRight: "auto",
            padding: "2%",
            border: "1px solid black",
            borderRadius: "4px",
           backgroundColor: "white",
           height:"500px",
           width:"96%"
        }}>
         <FormHeader heading="Create New Event"/>
         <form style={{
            padding: "0%",
            display: "flex",
            flexDirection:" column",
            justifyContent: "space-between",
            width:"100%",
            marginTop:"20px",
            height:"400px"
         }}>
            <FormElement type="text" name="Event Title" value={inputs.value} onChange={handleChange}
            onKeyDown={(e)=>{if(e.key==="Enter")handleEnter(e)}}/>
            <FormElement type="date" name = "Date" display="Pick a date" value={inputs.value}
             style={{height:"25px", width:"80px" ,marginLeft:"4%"}}
            onChange={handleChange} onKeyDown={(e)=>{if(e.key==="Enter")handleEnter(e)}} />
            <FormElement type="text" name="Location" value={inputs.value} onChange={handleChange}
            onKeyDown={(e)=>{if(e.key==="Enter")handleEnter(e)}}/>
            <label style={{display:"block"}}>
                Description:

                <textarea name="Description" value={inputs.Description} 
                onChange={handleChange} style={{width:"94%",marginLeft:"auto",marginRight:"auto"}}></textarea>
            </label>
         </form>
        </div>
    );
}
export default CreateEvent;
