import { useState } from 'react';
import FormHeader from './components/FormHeader';
import FormFooter from './components/FormFooter';
import FormElement from './components/FormElement';
import './FormStyle.css';
/*
import './Register.css';
*/
/*
function Header(){
    return(
    <div className="header">
    <h3>Register</h3>
    <div>Create your account</div>
    </div>
    );
}
    */
function Form(){
    const [inputs, setInputs]=useState({})
    function handleChange(e){
        const name=e.target.name;
        const value=e.target.value;
        setInputs({...inputs, [name]:value});
    }
    function handleEnter(e){
        if(e.target.name==="Name"){
            document.getElementsByName('Email')[0].focus();
        }
        if(e.target.name==="Email"){
            document.getElementsByName('Password')[0].focus();
        }
        if(e.target.name==="Password"){
            document.getElementsByName('Confirm Password')[0].focus();
        }
        if(e.target.name==="Confirm Password"){
            // To je isto kao i pritisak na Sign in
        }

    }
    return (
        <div className="form" style={{
            padding: "0%",
            display: "flex",
            flexDirection:" column",
            justifyContent: "space-between",
            width:"100%",
            marginTop:"20px",
            height:"400px"
        }}>
        <label>
            Name:<br/>
            <input 
                type="text"
                name="name"
                className='formElement'
                placeholder='John Doe'
                value={inputs.name}
                onChange={handleChange}
                onKeyDown={(e)=>{if(e.key==="Enter")handleEnter(e)}}
            />

        </label>
        <label>
            Email:<br/>
            <input 
                type="email"
                name="email"
                className='formElement'
                placeholder='m@example.com'
                value={inputs.email}
                onChange={handleChange}
                onKeyDown={(e)=>{if(e.key==="Enter")handleEnter(e)}}
            />
        </label>
        <label>
            Password:<br/>
            <input 
                type="password"
                name="password"
                className='formElement'
                value={inputs.password}
                onChange={handleChange}
                onKeyDown={(e)=>{if(e.key==="Enter")handleEnter(e)}}
            />
        </label>
        <label>
            Confirm Password:<br/>
            <input 
                type="password"
                name="confirmPassword"
                className='formElement'
                value={inputs.confirmPassword}
                onChange={handleChange}
                onKeyDown={(e)=>{if(e.key==="Enter")handleEnter(e)}}
            />
        </label>
        <input type="submit" value="Register" className="submitButton formElement"/>
        </div>
    );
}
function Footer(){
    let question="Already have an account? ";
    return (
        <div className='footer'>
            {question}<a href="./SignIn">Sign in</a>
        </div>
    );
}
function Register(){
    return(
        <div className='all'>
        <FormHeader />
        <Form/>
        <Footer />
        </div>
    );
}
export default Register;
