import { useState } from 'react';
import './FormStyle.css';
import './Register.css';
function SignInHeader(){
    return(
    <div className="header">
    <h3>Register</h3>
    <div>Create your account</div>
    </div>
    );
}
function Form(){
    const [inputs, setInputs]=useState({})
    function handleChange(e){
        const name=e.target.name;
        const value=e.target.value;
        setInputs({...inputs, [name]:value});
    }
    function handleEnter(e){
        if(e.target.name==="name"){
            document.getElementsByName('email')[0].focus();
        }
        if(e.target.name==="email"){
            document.getElementsByName('password')[0].focus();
        }
        if(e.target.name==="password"){
            document.getElementsByName('confirmPassword')[0].focus();
        }
        if(e.target.name==="confirmPassword"){
            // To je isto kao i pritisak na Sign in
        }

    }
    return (
        <div className="form">
        <label>
            Name:<br/>
            <input 
                type="text"
                name="name"
                className='formElement'
                placeholder='John Doe'
                value={inputs.name}
                onChange={handleChange}
                onKeyDown={(e)=>{if(e.key==="Enter")handleEnter(e)}}
            />

        </label>
        <label>
            Email:<br/>
            <input 
                type="email"
                name="email"
                className='formElement'
                placeholder='m@example.com'
                value={inputs.email}
                onChange={handleChange}
                onKeyDown={(e)=>{if(e.key==="Enter")handleEnter(e)}}
            />
        </label>
        <label>
            Password:<br/>
            <input 
                type="password"
                name="password"
                className='formElement'
                value={inputs.password}
                onChange={handleChange}
            />
        </label>
        <label>
            Confirm Password:<br/>
            <input 
                type="password"
                name="confirmPassword"
                className='formElement'
                value={inputs.password}
                onChange={handleChange}
            />
        </label>
        <input type="submit" value="Register" className="submitButton formElement"/>
        </div>
    );
}
function Footer(){
    let question="Already have an account? ";
    return (
        <div className='footer'>
            {question}<a href="./Sign in">Sign in</a>
        </div>
    );
}
function Register(){
    return(
        <div className='all'>
        <SignInHeader/>
        <Form/>
        <Footer />
        </div>
    );
}
export default Register;
